    public List<List<Integer>> threeSum(int[] nums) {
        int[] sortedNums = Arrays.stream(nums).sorted().toArray();
        int length = sortedNums.length;
        Set<List<Integer>> resultSet = new HashSet<>();
        for (int i = 0; i < length; i++) {
            int j = i + 1;
            int k = length - 1;
            int elementAtI = sortedNums[i];
            while (j < k) {
                int elementAtJ = sortedNums[j];
                int elementAtK = sortedNums[k];
                if (j == i) {
                    j++;
                    continue;
                }
                if (k == i) {
                    k--;
                    continue;
                }
                if (elementAtI + elementAtJ + elementAtK > 0) {
                    k--;
                } else if (elementAtI + elementAtJ + elementAtK < 0) {
                    j++;
                } else {
                    resultSet.add(List.of(elementAtI, elementAtJ, elementAtK));
                    j++;
                }
            }
        }
        return new ArrayList<>(resultSet);
    }


